import React, { Component } from 'react';
import Appbar from './Appbar';
import AppDrawer from './AppDrawer';
import AppMain from './AppMain';

import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

const styles = theme => ({
  root: {
    display: 'flex',
  },
});

class Shell extends Component {
  state = {
    drawerOpen: true,
    mobileDrawerOpen: false,
  };

  componentDidMount() {
    window.addEventListener('resize', this.updateDrawer);
  }

  componentWillUnmount() {
    window.removeEventListener('resize', this.updateDrawer);
  }

  updateDrawer = () => {
    if (window.innerWidth >= 960 && !this.state.mobileDrawerOpen) {
      this.closeMobileDrawer();
    }
  };

  toggleDrawer = () => {
    this.setState({ drawerOpen: !this.state.drawerOpen });
  };

  openDrawer = () => {
    this.setState({ drawerOpen: true });
  };  

  closeDrawer = () => {
    this.setState({ drawerOpen: false });
  };  

  toggleMobileDrawer = () => {
    this.setState({ mobileDrawerOpen: !this.state.mobileDrawerOpen });
  };

  openMobileDrawer = () => {
    this.setState({ mobileDrawerOpen: true });
  }

  closeMobileDrawer = () => {
    this.setState({ mobileDrawerOpen: false });
  }

  render() {
    const { classes,  } = this.props;

    return (
      <div className={classes.root}>
        <Appbar title="App Title" toggleDrawer={this.toggleDrawer} toggleMobileDrawer={this.toggleMobileDrawer} />
        <AppDrawer open={this.state.drawerOpen} mobileOpen={this.state.mobileDrawerOpen}
          openMobileDrawer={this.openMobileDrawer} closeMobileDrawer={this.closeMobileDrawer}>
          waddup
        </AppDrawer>
        <AppMain open={this.state.drawerOpen}>
          Yo
        </AppMain>
      </div>
    );
  }
}

Shell.propTypes = {
  classes: PropTypes.object.isRequired,
  theme: PropTypes.object.isRequired,
};

export default withStyles(styles, { withTheme: true })(Shell);
