import React, { Component } from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import { withStyles } from '@material-ui/core/styles';

const drawerWidth = 240;

const styles = theme => ({
  container: {
    position: 'relative', 
    width: '100%', 
    display: 'flex', 
    justifyContent: 'center',
  },
  hero: {
    position: 'fixed', 
    backgroundColor: '#a8c4c4', 
    height: 800, 
    paddingTop: 250, 
    width: '100%', 
    textAlign: 'center'
  },
  heroImage: {
    width: 200, 
    height: 200, 
    borderRadius: 25,
  },
  heroText: {
    fontSize: 64,
  },
  content: {
    width: '100%', 
    position: 'absolute', 
    top: 800, 
    backgroundColor: '#fff',
  },
  header: {
    fontSize: 32, 
    color: '#00695c',
  },
  frameContainer: {
    height: 600, 
    width: '100%', 
    background: '#efefef', 
    position: 'relative',
  },
  _scrollBanner: {
    width: '100%',
    backgroundColor: '#565656', 
    color: '#ffffff', 
    fontSize: 18,
    display: 'flex', 
    justifyContent: 'center', 
    alignItems: 'center', 
    zIndex: 2, 
    cursor: 'pointer', 
    userSelect: 'none',
    position: 'absolute',
    left: 0,
  },
  _scrollBannerStickyShift: {
    position: 'fixed',
    top: 48,
    width: `calc(100% - ${drawerWidth}px)`,
    left: drawerWidth,
  },
  _scrollBannerSticky: {
    transition: theme.transitions.create(['width', 'left'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    position: 'fixed',
    top: 48,
    // width: `calc(100% - ${drawerWidth}px)`,
    // left: drawerWidth,
  },
  _scrollOverlay: {
    height: 600, 
    width: '100%', 
    backgroundColor: 
    'rgba(71, 71, 71, 0.4)', 
    top: 0, 
    left: 0,
    position: 'absolute',
  },
});

