    if (window.innerWidth < 960) {
      this.setState(state => ({ mobileOpen: !state.mobileOpen }));
    } else {
      if (this.state.open) {
        this.setState({ open: false });
      } else {
        this.setState({ open: true });
      }
    }
  };

