import React, { Component } from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import { withStyles } from '@material-ui/core/styles';
import logo from './logo.png';

const drawerWidth = 240;

const styles = theme => ({
  container: {
    position: 'relative', 
    width: '100%', 
    display: 'flex', 
    justifyContent: 'center',
  },
  hero: {
    position: 'fixed', 
    backgroundColor: '#a8c4c4', 
    width: '100%', 
    textAlign: 'center',
    [theme.breakpoints.down('lg')]: {
      height: 800, 
      paddingTop: 250, 
    },
    [theme.breakpoints.down('md')]: {
      height: 650, 
      paddingTop: 150, 
    },
    [theme.breakpoints.down('sm')]: {
      height: 450, 
      paddingTop: 100, 
    },
  },
  heroImage: {
    [theme.breakpoints.down('lg')]: {
      width: 200, 
      height: 200, 
      borderRadius: 25,
    },
    [theme.breakpoints.down('md')]: {
      width: 150, 
      height: 150, 
      borderRadius: 15,
    },
    [theme.breakpoints.down('sm')]: {
      width: 100, 
      height: 100, 
      borderRadius: 9,
    },
  },
  heroText: {
    [theme.breakpoints.down('lg')]: {
      fontSize: 64,
    },
    [theme.breakpoints.down('md')]: {
      fontSize: 56,
    },
    [theme.breakpoints.down('sm')]: {
      fontSize: 42,
    },
  },
  content: {
    width: '100%', 
    position: 'absolute',
    backgroundColor: '#ffffff',
    [theme.breakpoints.down('lg')]: {
      top: 800,
    },
    [theme.breakpoints.down('md')]: {
      top: 650,
    },
    [theme.breakpoints.down('sm')]: {
      top: 450,
    },
  },
  header: {
    color: '#00695c',
    [theme.breakpoints.down('lg')]: {
      fontSize: 32, 
    },
    [theme.breakpoints.down('md')]: {
      fontSize: 28,
    },
    [theme.breakpoints.down('sm')]: {
      fontSize: 26,
    },
  },
  frameContainer: {
    height: 600, 
    width: '100%', 
    background: '#efefef', 
    position: 'relative',
  },
  _scrollBanner: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    width: '100vw',
    backgroundColor: '#565656', 
    color: '#ffffff', 
    fontSize: 18,
    display: 'flex', 
    justifyContent: 'center', 
    alignItems: 'center', 
    zIndex: 2, 
    cursor: 'pointer', 
    userSelect: 'none',
    position: 'absolute',
    [theme.breakpoints.up('md')]: {
      marginLeft: -drawerWidth/2,
    },
  },
  _scrollBannerShift: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: 0,
  },
  _scrollBannerSticky: {
    position: 'fixed',
    top: 48,
  },
  _scrollOverlay: {
    height: 600, 
    width: '100%', 
    backgroundColor: 
    'rgba(71, 71, 71, 0.4)', 
    top: 0, 
    left: 0,
    position: 'absolute',
  },
});

class Home extends Component {
  constructor(props) {
    super(props);
    this.lastY = 0;
    this.ticking = false;
    this.state = {
      overlay: true,
      sticky: false,
    };
  }

  componentDidMount() {
    window.addEventListener('scroll', this.handleScroll);
    this.iframeContainerTop = this.iframeContainer.getBoundingClientRect().top - 48 + window.pageYOffset;
    this.iframeContainerBottom = this.iframeContainerTop + 600 - 32;
  }

