import React, { Component } from 'react';
import { Link, Switch, Route } from 'react-router-dom';

import Appbar from './Appbar';
import AppDrawer from './AppDrawer';
import AppContent from './AppContent';

import Home from './Home';
import Contact from './Contact';
import NotFound from './NotFound';

import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';

import Icon from '@material-ui/core/Icon';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';

const styles = theme => ({
  root: {
    display: 'flex',
  },
});

class Shell extends Component {
  state = {
    route: this.props.route,
    open: true,
    mobileOpen: false,
  };

  componentWillMount() {
    window.addEventListener('resize', this.handleResize);
  }

  componentWillUnmount() {
    window.removeEventListener('resize', this.handleResize);
 }

 componentDidUpdate(prevProps) {
  if (this.props.route !== prevProps.route) {
    this.setRoute(this.props.route);
  }
}

setRoute = (route) => {
  this.setState({ route: route });
}

  handleDrawerOpen = () => {
    this.setState({ open: true });
  };

  handleDrawerClose = () => {
    this.setState({ open: false });
  };

  handleDrawerToggle = () => {
    if (window.innerWidth < 960) {
      this.setState(state => ({ mobileOpen: !state.mobileOpen }));
    } else {
      if (this.state.open) {
        this.setState({ open: false });
      } else {
        this.setState({ open: true });
      }
    }
  };

  handleResize = () => {
    if (window.innerWidth >= 960 && this.state.mobileOpen) {
      this.setState(state => ({ mobileOpen: false }));
    } 
  };

  render() {
    const { classes } = this.props;
    const { route, open, mobileOpen } = this.state;

    return (
      <div className={classes.root}>
        <CssBaseline />
        <Appbar title="Title" logoSrc={process.env.PUBLIC_URL + '/logo.png' } handleDrawerToggle={this.handleDrawerToggle} 
          value={25} hidden={false} />
        <AppDrawer open={open} mobileOpen={mobileOpen} handleDrawerToggle={this.handleDrawerToggle}>
          <List>
            <ListItem dense button key="home" component={Link} to="/" active>
              <Icon fontSize="small">home</Icon> 
              <ListItemText primary="Home" />
            </ListItem>
            <ListItem dense button key="contact" component={Link} to="/contact">
              <Icon fontSize="small">mail_outline</Icon> 
              <ListItemText primary="Contact" />
            </ListItem>
          </List>
        </AppDrawer>
        <AppContent open={open}>
          <Switch>
            <Route exact path='/' render={(props) => <Home {...props} callback={() => this.stopLoad()} />} />
            <Route exact path='/contact' render={(props) => <Contact {...props} callback={() => this.stopLoad()} />} />
            <Route path='*' exact={true} component={NotFound} />
          </Switch>
        </AppContent>
      </div>
    );
  }
}

Shell.propTypes = {
  classes: PropTypes.object.isRequired,
  theme: PropTypes.object.isRequired,
};

export default withStyles(styles, { withTheme: true })(Shell);
