import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import Hidden from '@material-ui/core/Hidden';
import Toolbar from '@material-ui/core/Toolbar';
import Divider from '@material-ui/core/Divider';

const drawerWidth = 240;

const styles = theme => ({
  drawer: {
    [theme.breakpoints.up('md')]: {
      width: drawerWidth,
      flexShrink: 0,
    },
  },
  drawerPaper: {
    width: drawerWidth,
  },
  _drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  _toolbar: {
    marginBottom: 7,
  },
});

class AppDrawer extends Component {

  render() {
    const { classes, open=true, mobileOpen=false, drawerItems={} } = this.props;



    return (
      <nav className={classes.drawer}>
        {/* The implementation can be swapped with js to avoid SEO duplication of links. */}
        <Hidden mdUp implementation="css">
          <Drawer
            transitionDuration={{enter: 100, exit: 70}}
            container={this.props.container}
            variant="temporary"
            anchor="left"
            open={mobileOpen}
            onClose={() => this.props.handleDrawerToggle()}
            classes={{
              paper: classes.drawerPaper,
            }}
          >
            <Toolbar variant="dense" disableGutters></Toolbar>
            <Divider />
            {this.props.children}
          </Drawer>
        </Hidden>
        <Hidden smDown implementation="css">
          <Drawer
            className={classes._drawer}
            variant="persistent"
            anchor="left"
            open={open}
            classes={{
              paper: classes.drawerPaper,
            }}
          >
            <div className={classes.toolbar} />
            <Toolbar className={classes._toolbar} variant="dense" disableGutters></Toolbar>
            <Divider />
            {this.props.children}
          </Drawer>
        </Hidden>
      </nav>
    );
  }
}

AppDrawer.propTypes = {
  classes: PropTypes.object.isRequired,
  theme: PropTypes.object.isRequired,
};

export default withStyles(styles, { withTheme: true })(AppDrawer);
