import React, { Component } from 'react';
import ReactDOM from 'react-dom';

class Home extends Component {

  state = {
    inFrame: false,
    keyDown: false,
  };

  componentDidMount() {
    document.body.addEventListener('keydown', this.handleKeyDown);
    document.body.addEventListener('keyup', this.handleKeyUp);
  }

  componentWillUnmount() {
    document.body.removeEventListener('keydown', this.handleKeyDown);
    document.body.removeEventListener('keyup', this.handleKeyUp);
  }

  handleKeyDown = (e) => {
    e.preventDefault();
    if (e.key === 'Shift') this.setState({ keyDown: true });
  };

  handleKeyUp = (e) => {
    e.preventDefault();
    if (e.key === 'Shift') this.setState({ keyDown: false });
    if (document.activeElement === ReactDOM.findDOMNode(this.refs.iframe)) {
      console.log('asdf');
    }
  };

  handleMouseEnter = (e) => {
    e.preventDefault();
    this.setState({ inFrame: true });
  };

  handleMouseLeave = (e) => {
    e.preventDefault();
    this.setState({ inFrame: false });
  };

  handleBlur = (e) => {
    console.log('focus in');
  };

  handleIframeLoad = () => {
    console.log('iframe done');
  };

  render() {
    return (
      <div style={{position: 'relative', width: '100%', display: 'flex', justifyContent: 'center'}} tabIndex="0">
        <div style={{position: 'fixed', backgroundColor: '#a8c4c4', height: 800, paddingTop: 250, width: '100%', textAlign: 'center'}}>
          <img style={{width: 200, height: 200, borderRadius: 25}} src={process.env.PUBLIC_URL + '/logo.png'} alt="banner" />
          <h1 style={{fontSize: 64}}>Title</h1>
        </div>
        <div style={{width: '100%', position: 'absolute', top: 800, backgroundColor: '#fff'}}>
          <h2 style={{fontSize: 32, color: '#00695c'}}>'Sup, ðŸ…±?</h2>
          asdf<br/>asdf<br/>asdf<br/>asdf<br/>asdf<br/>asdf<br/>asdf<br/>

          <div style={{height: 600, background: '#efefef', position: 'relative'}} 
            onMouseEnter={this.handleMouseEnter}
            onMouseLeave={this.handleMouseLeave}>
            {'key: ' + this.state.keyDown + ' frame: ' + this.state.inFrame}
            {!(this.state.inFrame && this.state.keyDown) && <div style={{height: 600, width: '100%', backgroundColor: 'rgba(71, 71, 71, 0.65)', color: '#fff', fontSize: 24, display: 'flex', justifyContent: 'center', paddingTop: 250, position: 'absolute'}}>Hold shift to interact</div>}
            <iframe ref="iframe" style={{pointerEvents: (this.state.inFrame && this.state.keyDown) ? 'auto' : 'none'}} src={process.env.PUBLIC_URL +  '/react360/index.html'} height="600" width="100%" frameBorder="0" title="360-demo" 
              onLoad={this.handleIframeLoad}
              onBlur={this.handleBlur} />
          </div>   
        </div>
      </div>
    );
  }
}

export default Home;
