import React, { Component } from 'react';

class Home extends Component {
  constructor(props) {
    super(props);
    this.lastY = 0;
	  this.ticking = false;
  }


  state = {
    overlay: true,
    sticky: false,
  };

  componentDidMount() {
    window.addEventListener('scroll', this.handleScroll);
    this.iframeContainerTop = this.iframeContainer.getBoundingClientRect().top - 48;
    this.iframeContainerBottom = this.iframeContainerTop + 600 - 32;
  }

  componentWillUnmount() {
    window.removeEventListener('scroll', this.handleScroll);
  }

  toggleOverlay = () => {
    this.setState({ overlay: !this.state.overlay });

  };

  handleScroll = () => {
    this.lastY = window.scrollY;
	  if(!this.ticking) {
      requestAnimationFrame(this.update);
    }
    this.ticking = true;
  };

  update = () => {
    this.ticking = false;
    this.currentY = this.lastY;
    if (this.currentY >= this.iframeContainerTop && this.currentY <= this.iframeContainerBottom) {
      if (!this.state.sticky) this.setState({ sticky: true });
    } else {
      if (this.state.sticky) this.setState({ sticky: false });
    }
  }

  handleIframeLoad = () => {

  };

